CREATE MEMORY TABLE "PUBLIC"."SANDBOX" COMMENT '샘플 테이블' (
    "ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1 RESTART WITH 7) DEFAULT ON NULL COMMENT '식별자' NOT NULL,
    "NAME" CHARACTER VARYING(50) COMMENT '이름' NOT NULL,
    "AGE" TINYINT COMMENT '나이' NOT NULL,
    "CATEGORY" CHARACTER VARYING(15) DEFAULT 'NORMAL' COMMENT '유형' NOT NULL,
    "DELETED" BOOLEAN DEFAULT 0 COMMENT '삭제여부' NOT NULL,
    "CREATE_DATE" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP COMMENT '생성일' NOT NULL,
    "UPDATE_DATE" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP COMMENT '최종수정일' NOT NULL
);
ALTER TABLE "PUBLIC"."SANDBOX" ADD CONSTRAINT "PUBLIC"."CONSTRAINT_9" PRIMARY KEY("ID");

CREATE MEMORY TABLE "PUBLIC"."BOARD" COMMENT '게시물 테이블' (
    "BOARD_ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1 RESTART WITH 6) DEFAULT ON NULL COMMENT '식별자' NOT NULL,
    "BOARD_TITLE" CHARACTER VARYING(250) COMMENT '제목' NOT NULL,
    "BOARD_USE_YN" BOOLEAN DEFAULT 0 COMMENT '삭제여부' NOT NULL,
    "CREATE_DATE" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP COMMENT '생성일' NOT NULL,
    "UPDATE_DATE" TIMESTAMP(6) DEFAULT CURRENT_TIMESTAMP COMMENT '최종수정일' NOT NULL
);
ALTER TABLE "PUBLIC"."BOARD" ADD CONSTRAINT "PUBLIC"."CONSTRAINT_3" PRIMARY KEY("BOARD_ID");

CREATE MEMORY TABLE "PUBLIC"."BOARD_CONTENTS" COMMENT '게시물 테이블' (
    "BOARD_ID" BIGINT COMMENT '게시물 식별자' NOT NULL,
    "BOARD_CONTENTS" CHARACTER LARGE OBJECT COMMENT '게시물 컨텐츠 내용' NOT NULL
);
ALTER TABLE "PUBLIC"."BOARD_CONTENTS" ADD CONSTRAINT "PUBLIC"."CONSTRAINT_7" PRIMARY KEY("BOARD_ID");

CREATE MEMORY TABLE "PUBLIC"."BOARD_ATTACHMENT" COMMENT '게시물 첨부파일 테이블' (
    "BOARD_ATTACHMENT_ID" BIGINT GENERATED BY DEFAULT AS IDENTITY(START WITH 1 RESTART WITH 6) DEFAULT ON NULL COMMENT '게시물 첨부파일 식별자' NOT NULL,
    "BOARD_ID" BIGINT COMMENT '게시물 식별자' NOT NULL,
    "BOARD_ATTACHMENT_PATH" CHARACTER VARYING(150) COMMENT '첨부파일경로' NOT NULL,
    "BOARD_ATTACHMENT_NAME" CHARACTER VARYING(100) COMMENT '첨부파일명' NOT NULL
);
ALTER TABLE "PUBLIC"."BOARD_ATTACHMENT" ADD CONSTRAINT "PUBLIC"."CONSTRAINT_95" PRIMARY KEY("BOARD_ATTACHMENT_ID");
